mapper.update:
UPDATE t_home
start_time=#{startTime}, is_end=#{isEnd}, is_reservation=#{isReservation}, res_time=#{resTime}, user_id=#{userId}, has_user=#{hasUser}, technician_id=#{technicianId}, home_num=#{homeNum}, size=#{size},
WHERE id=#{id}

UPDATE t_home
set user_id=0
WHERE user_id=#{userId}

UPDATE t_home
set user_id=#{userId}
WHERE home_num=#{homeNum}
