mapper.select:
SELECT id, user_id as userId, grade, price, point, discount, card_name as cardName, card_num as cardNum FROM member_card
card_num like CONCAT('%',#{query},'%')
and grade=#{grade}

mapper.update:
UPDATE member_card
price=#{price}, point=#{point}, discount=#{discount}, balance=#{balance}, point_balance=#{pointBalance},
WHERE id=#{id}
